DROP TABLE IF EXISTS `sys_dblog`;
CREATE TABLE IF NOT EXISTS `sys_dblog` (
  `l_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '日志id',
  `l_puid` int(10) NOT NULL COMMENT '操作人id',
  `l_puname` varchar(20) NOT NULL COMMENT '操作人',
  `l_date` datetime NOT NULL COMMENT '操作日期',
  `l_type` varchar(20) NOT NULL COMMENT 'sql分类',
  `l_file` varchar(200) NOT NULL COMMENT '来源文件',
  `l_sql` varchar(2000) NOT NULL COMMENT 'sql语句',
  `l_res` varchar(20) NOT NULL COMMENT '执行结果是否成功',
  PRIMARY KEY (`l_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='数据库增删改操作日志表' AUTO_INCREMENT=98 ;

