INSERT INTO `p_user` VALUES ('2', '0', 'xufei', 'c4ca4238a0b923820dcc509a6f75849b', '徐飞', '/uploads/userico/徐飞_2.jpg', '', '', '', '', '', '', '2013-06-15 00:57:36', '0', '');
