INSERT INTO `web_product` VALUES ('2', '1', '营销型网站（套餐一）', '初创业、成长型企业,以展示公司形象及服务产品等信息为主.标准化企业网站风格设计', '999', '8569', '0', 'YX001', 'YX001', '1', '138', '0', '个', '3000', '5000', '5500', '5980', '8999', '/uploads/product/20130619/20130619004244_20187.jpg', '<table border=\"0\" cellspacing=\"1\" cellpadding=\"2\" width=\"96%\" bgcolor=\"#cccccc\" align=\"center\" style=\"margin:0px auto;padding:0px;font-size:12px;color:#333333;font-family:宋体;line-height:24px;\" class=\"ke-zeroborder\">\n	<tbody style=\"margin:0px auto;padding:0px;\">\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" width=\"13%\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">适用企业</span> \n			</td>\n			<td width=\"87%\" style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">初创业、成长型企业,以展示公司形象及服务产品等信息为主.</span> \n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">网站设计</span> \n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">标准化企业网站风格设计</span> \n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				系统功能\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				一、网站结构系统：栏目管理，无限级分类；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n二、单页图文系统：自由强大，所见即所得；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n三、新闻发布系统：在线发布管理各类新闻；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n四、产品展示系统：在线发布管理公司产品；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n五、友情链接系统：在线发布管理友情链接；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n六、在线留言系统：访问者可留言或提问；\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">增值功能模块</span> \n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				访问统计系统：对访客进行统计分析&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n在线即时通讯：在线QQ，MSN，旺旺&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n数据库备份管理 系统操作日志\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				SEO面向搜索引擎优化\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				合理专业的站内结构有利于 多关键字面向BAIDU/GOOGLE进行优化 &nbsp;\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				制作周期\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				5-7个工作日&nbsp;\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				售后服务\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				网站终身质量保证及免费维护 工作时间电话技术支持免费协助办理工信部网站ICP备案\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				质量保证\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				一年免费的一对一售后服务保障\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#FF0000;font-weight:bold;\">\n				空间域名\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<p style=\"margin:0px auto;padding:0px;\">\n					国际定级域名1个(.com/.net/.com.cn/.cn等)<br style=\"margin:0px auto;padding:0px;\" />\n500M网站空间万网\n				</p>\n			</td>\n		</tr>\n	</tbody>\n</table>', '成都网站建设,成都网站制作,成都做网站,成都网站优化,成都网页设计,成都网页制作', '成都网站建设服务热：13688387776——凡色100网站建设公司是一家专业从事成都网站建设、成都网站制作、成都网页设计、成都网页制作、成都做网站及成都网站优化为一体的网站建设公司,凭借着多年网站制作经验,为成都大中小企业提供高端网页制作及成都网站建设成都网站优化服务,凡色100，dacms', '2013-06-12 22:01:04', '2013-06-19 00:42:44');
INSERT INTO `web_product` VALUES ('3', '1', '营销增强型（套餐二）', '', '999', '997', '1', 'YX002', 'YX002', '2', '0', '0', '个', '5000', '0', '0', '8980', '12000', '', '<table border=\"0\" cellspacing=\"1\" cellpadding=\"2\" width=\"96%\" bgcolor=\"#cccccc\" align=\"center\" style=\"margin:0px auto;padding:0px;font-size:12px;color:#333333;font-family:宋体;line-height:24px;\" class=\"ke-zeroborder\">\n	<tbody style=\"margin:0px auto;padding:0px;\">\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" width=\"13%\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">适用企业</span>\n			</td>\n			<td width=\"87%\" style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">发展中、已具一定规模企业对网站的设计和功能有较高要求.</span>\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">网站设计</span>\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">标准化企业网站风格设计</span>\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				系统功能\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				一、网站结构系统：栏目管理，无限级分类；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n二、单页图文系统：自由强大，所见即所得；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n三、新闻发布系统：在线发布管理各类新闻；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n四、人才招聘系统：在线发布管理人才信息；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n五、产品展示系统：在线发布管理公司产品；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n六、下载管理系统：管理可供下载的文件；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n七、友情链接系统：在线发布管理友情链接；&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n八、在线留言系统：访问者可留言或提问；\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				<span style=\"margin:0px auto;padding:0px;font-family:Verdana;\">增值功能模块</span>\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				访问统计系统：对访客进行统计分析<br style=\"margin:0px auto;padding:0px;\" />\n在线即时通讯：在线QQ，MSN，旺旺&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n数据库备份管理：安全备份网站数据&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n系统操作日志：记录网站登陆用户&nbsp;<br style=\"margin:0px auto;padding:0px;\" />\n管理员用户管理：管理网站后台管理员\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				SEO面向搜索引擎优化\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				合理专业的站内结构有利于 多关键字面向BAIDU/GOOGLE进行优化 &nbsp;\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				制作周期\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				8-15个工作日&nbsp;\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				售后服务\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				网站终身质量保证及免费维护 工作时间电话技术支持免费协助办理工信部网站ICP备案\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#4AAF4A;\">\n				质量保证\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				一年免费的一对一售后服务保障\n			</td>\n		</tr>\n		<tr bgcolor=\"#ffffff\" style=\"margin:0px auto;padding:0px;\">\n			<td valign=\"center\" style=\"margin:0px auto;padding:5px;line-height:1.7em;text-align:center;color:#FF0000;font-weight:bold;\">\n				空间域名\n			</td>\n			<td style=\"margin:0px auto;padding:5px 3px 5px 5px;line-height:1.7em;\">\n				<p style=\"margin:0px auto;padding:0px;\">\n					国际定级域名1个(.com/.net/.com.cn/.cn等)<br style=\"margin:0px auto;padding:0px;\" />\n1000M网站空间万网 + 1G企业邮箱&nbsp;\n				</p>\n			</td>\n		</tr>\n	</tbody>\n</table>', '', '', '2013-06-12 22:39:08', '2013-06-12 22:39:08');
