INSERT INTO `web_producttype` VALUES ('4', '0', '网页设计', '50', '10', 'SINGLEPAGE', '', '<span style=\"font-family:Verdana, Arial, sans-serif;font-size:14px;line-height:20px;white-space:normal;background-color:#FBFDFF;\">关键词:16个天马行空令人印象深刻的CSS3&amp;HTML5尝试</span><br style=\"font-family:Verdana, Arial, sans-serif;font-size:14px;line-height:20px;white-space:normal;background-color:#FBFDFF;\" />\n<span style=\"font-family:Verdana, Arial, sans-serif;font-size:14px;line-height:20px;white-space:normal;background-color:#FBFDFF;\">关于CSS3我们已经说了很多年了。现在终于看到很多童鞋开始深入研究并将一些高效有意思的功能放进各自的项目中去，看了很高兴。这两年随着HTML5的迅速崛起。这两者的结合将是未来不可避免的一个趋势。网页设计在沉静的N多年后终于开始诠释了设计的概念。很多公司也开始考虑深入交互和体验。这是互联网的一小步，网页发展的一大步</span><span id=\"more-9428\" style=\"font-family:Verdana, Arial, sans-serif;font-size:14px;line-height:20px;white-space:normal;background-color:#FBFDFF;\"></span><span style=\"font-family:Verdana, Arial, sans-serif;font-size:14px;line-height:20px;white-space:normal;background-color:#FBFDFF;\">。关于未来总是在超乎我们想象中的未来。很多可能都是在现在懵懂后的不断尝试中发现和得到的。今天我们要分享的是16个天马行空的CSS3&amp;HTML5的尝试。这些尝试让人印象深刻。希望大家能够喜欢。</span>', '', '', '', '');
