-- Records of sys_collect
-- ----------------------------

-- ----------------------------
-- Table structure for `sys_collectrule`
-- ----------------------------
DROP TABLE IF EXISTS `sys_collectrule`;
CREATE TABLE `sys_collectrule` (
  `r_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '文章采集规则id',
  `r_name` varchar(200) NOT NULL COMMENT '文章采集规则名称',
  `r_pagecode` varchar(50) NOT NULL COMMENT '采集页面编码',
  `r_urltype` varchar(50) NOT NULL COMMENT '网址类型',
  `r_urlsource` text NOT NULL COMMENT '多网址型来源网址',
  `r_urlsource2` varchar(2000) NOT NULL COMMENT '连续型网址来源',
  `r_num1` int(10) NOT NULL COMMENT '连续型网址来源开始序号',
  `r_num2` int(10) NOT NULL COMMENT '连续型网址来源结束序号',
  `r_step` int(10) NOT NULL COMMENT '连续型网址来源递增步长',
  `r_urlsource3` varchar(2000) NOT NULL COMMENT '单网址型网址来源',
  `r_urlsource4` varchar(2000) NOT NULL COMMENT 'RSS型网址来源',
  `r_urlallowed` varchar(200) NOT NULL COMMENT '网址必须包含字符',
  `r_urlunallowed` varchar(200) NOT NULL COMMENT '网址不能包含字符',
  `r_urlrange1` varchar(1000) NOT NULL COMMENT '网址来源范围开始位置',
  `r_urlrange2` varchar(1000) NOT NULL COMMENT '网址来源范围结束位置',
  `r_titlerule` text NOT NULL COMMENT '标题匹配规则',
  `r_titleclear` text NOT NULL COMMENT '标题清除字符规则',
  `r_keywordsrule` text NOT NULL COMMENT '关键词匹配规则',
  `r_keywordsclear` text NOT NULL COMMENT '关键词清除字符规则',
  `r_descriptionrule` text NOT NULL COMMENT '描述匹配规则',
  `r_descriptionclear` text NOT NULL COMMENT '描述清除字符规则',
  `r_contentrule` text NOT NULL COMMENT '文章内容匹配规则',
  `r_contentclear` text NOT NULL COMMENT '文章内容清除字符规则',
  `r_split` tinyint(4) NOT NULL COMMENT '内容是否分页',
  `r_splittype` varchar(10) NOT NULL COMMENT '内容分页方式',
  `r_splitrange1` varchar(1000) NOT NULL COMMENT '内容分页代码开始位置',
  `r_splitrange2` varchar(1000) NOT NULL COMMENT '内容分页代码结束位置',
  `r_splitnexttag` varchar(1000) NOT NULL COMMENT '内容分页下一页标志代码',
  `r_downloadimg` tinyint(4) NOT NULL COMMENT '是否下载图片',
  `r_date` datetime NOT NULL COMMENT '最近采集日期',
  PRIMARY KEY (`r_id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='文章采集规则表';

